import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import FormContainer from './FormContainer'
import axios from 'axios';

const LoginScreen = ({ location = {} }) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const redirect = location.search ? location.search.split('=')[1] : '/home';

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          'Content-type': 'application/json',
        },
      };

      const { data } = await axios.post('/api/users/login/', {
        username: email,
        password: password,
      }, config);

      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate(redirect || '/');
    } catch (error) {
      const errorMessage = error.response && error.response.data.detail ? error.response.data.detail : error.message;
      setError(errorMessage);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userInfo');
    navigate('/login');
  };

  return (
    <div>
      <FormContainer>
        <h1>Sign In</h1>
        {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
        <Form onSubmit={submitHandler}>
          <Form.Group controlId='email'>
            <Form.Label>Email Address</Form.Label>
            <Form.Control required type='email' placeholder='Enter Email' value={email} onChange={(e) => setEmail(e.target.value)}></Form.Control>
          </Form.Group>
          <Form.Group controlId='password'>
            <Form.Label>Password</Form.Label>
            <Form.Control required type='password' placeholder='Enter Password' value={password} onChange={(e) => setPassword(e.target.value)}></Form.Control>
          </Form.Group>
          <Button className='mt-3' type='submit' variant='primary'>Sign In</Button>
        </Form>
        <Row className='py-3'>
          <Col>New Customer? <Link to={redirect ? `/register?redirect=${redirect}` : '/register'}>Register</Link></Col>
        </Row>
      </FormContainer>
    </div>
  );
};

export default LoginScreen;
