import React, { useState, useEffect } from 'react';
import { Button, Card, Row, Col } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
function CartScreen() {


  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Fetch the current cart from localStorage
    const existingCart = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(existingCart);
  }, []);


  // Calculate total price
  const totalPrice = cart.reduce((total, item) => total + item.price * item.qty, 0);

  return (
    <div className='text-center'>
      <div className='mt-3'>
        <Link to='/spicy'>
          <Button style={{ marginLeft: '1250px' }}>Go Back</Button>
        </Link>
      </div>

      <div>
        <Row>
          <Col>

          <h1></h1>
          </Col>
        </Row>
      </div>




      <div style={{ marginBottom: '20px' }}>
        <Row>
          <Col>
            <Card className='m-auto' border="info" style={{ width: '40rem', height: '35rem' }}>
              <Card.Header style={{ color: 'blue', backgroundColor: 'skyblue' }} className='menu text-left'><b>Shopping Cart</b></Card.Header>
              <Card.Body>
                <Card.Title>Details</Card.Title>
                <Card.Text>
                  <ul className="list-group menu">
                    {cart.map((item) => (
                      <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {item.name}
                        <span>
                          Quantity: {item.qty}
                        </span>

                        <span>
                          Price: ${item.price * item.qty}
                        </span>
                      </li>
                    ))}
                  </ul>
                </Card.Text>
              </Card.Body>

              <div className="card-footer bg-transparent border-info menu">

                <Row>
                  <h6 style={{ marginLeft: '200px', color: 'red' }}><b>Total Price: ${totalPrice.toFixed(2)}</b></h6>
                </Row>
              </div>
            </Card>
          </Col>
        </Row>
      </div>


      {/* <h1></h1>
       */}



    </div>
  )
}

export default CartScreen
