import React, { useState, useEffect } from 'react';
import { Button,Card,Row,Col } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
function CartScreen() {
   

    const [cart, setCart] = useState([]);

  useEffect(() => {
    // Fetch the current cart from localStorage
    const existingCart = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(existingCart);
  }, []);


  return (
    <div className='text-center'>
        <div className='mt-3'>
            <Link to ='/spicy'>
        <Button style={{marginLeft:'1250px'}}>Go Back</Button>
        </Link>
        </div>

       <div style={{ marginBottom: '20px' }}>
        <Row>
          <Col>
        <Card className='m-auto' border="info" style={{ width: '27rem', height: '20rem' }}>
        <Card.Header style={{color:'violet'}} className='menu text-left'><b>Shopping Cart</b></Card.Header>
        <Card.Body>
          <Card.Title>Details</Card.Title>
          <Card.Text>
          <ul>
        {cart.map((item) => (
          <li key={item.id}>
            {item.name} - Quantity: {item.qty} - Price: ${item.price * item.qty}
          </li>
        ))}
      </ul>
          </Card.Text>
        </Card.Body>

        <div className="card-footer bg-transparent border-info">
         <h6 style={{marginRight:'355px'}}>Total - </h6> </div>
      </Card>
      </Col>
      </Row>
      </div>


     {/* <h1></h1>
       */}


   
    </div>
  )
}

export default CartScreen
