import React from 'react'
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Row,Col,Image,ListGroup,Button,Card } from 'react-bootstrap';
import products from '../products';

import Rating from './rating'


function SpicyScreen() {
  const numReviewsText = `${product.numReviews} review${product.numReviews !== 1 ? 's' : ''}`;

    const { id } = useParams();
    const product = products.find((p) => p._id === id);
  
  
  
  return (
    <div>
     <Link to="/spicy" style={{marginLeft:'1250px'}} className='btn btn-dark my-3' >Go Back</Link>
     <Row className='text-center'>
      
        <Col md={6}>
            <Image src={product.image} alt={product.name} style={{width:'500px', height:'500px', marginLeft:'90px'}}></Image>
        </Col>

        <Col md={3}>
          <ListGroup className='flush'>
            <ListGroup.Item>
              <h1>{product.name}</h1>
            </ListGroup.Item>


<ListGroup.Item>
  <Rating 
  value={product.rating}
  text={numReviewsText}
  color={"green"}>

  </Rating>
</ListGroup.Item>
          </ListGroup>
        </Col>
     </Row>
    </div>
  )
}

export default SpicyScreen
