import React, { useState } from 'react';

const Home = () => {
  const [isCelebrating, setIsCelebrating] = useState(false);

  const handleCelebration = () => {
    setIsCelebrating(true);

    // Reset the celebration after a duration (e.g., 2 seconds)
    setTimeout(() => {
      setIsCelebrating(false);
    }, 2000); // Duration in milliseconds (2 seconds in this case)
  };

  return (
    <div className="text-center">
      <h1
        className={isCelebrating ? 'celebrate' : ''}
        onClick={handleCelebration}
      >
        <i>
          <b>Welcome to KITCHENERS SPELL</b>
        </i>
      </h1>
    </div>
  );
};

export default Home;
