import React, {useState,useEffect} from 'react';

import { useParams,useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card,Form, FormGroup } from 'react-bootstrap';

import axios from 'axios';

import FormContainer from './FormContainer';

function LoginScreen() {

  const [email,setEmail]=useState('')
  const [password,setPassword]=useState('')
  const navigate = useNavigate();

  const submitHandler= async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('api/users/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        // Assuming your backend sends a token in the response
        const accessToken = data.access_token;

        // Store the token securely, e.g., in local storage
        localStorage.setItem('accessToken', accessToken);

        // Redirect the user to a protected route or update app state
        navigate.push('/dashboard');
      } else {
        // Handle unsuccessful login (e.g., show an error message)
        console.error('Login failed');
      }
    } catch (error) {
      console.error('An error occurred during login', error);
    }
  };

  return (
    <div>
      <FormContainer>
        <h1>Login</h1>
        <Form onSubmit={submitHandler}>
          <Form.Group controlId='email'>
          <Form.Label>Email Address </Form.Label>
          <Form.Control required type='email' placeholder='Enter Email' value={email} onChange={(e)=> setEmail(e.target.value)}></Form.Control>
          </Form.Group>

          <Form.Group controlId='password'>
                <Form.Label>Password</Form.Label>
                <Form.Control required type='password' placeholder='Enter Password' value={password} onChange={(e)=> setPassword(e.target.value)}></Form.Control>
              </Form.Group>

           <Button className='mt-3' type='submit' variant='primary'>Sign In</Button>
        </Form>

        <Row className='py-3'>
              <Col>
              New Customer? 
              {/* <Link to={redirect?`/register?redirect=${redirect}`:'/register'}>Register</Link> */}
              </Col>

          </Row>
      </FormContainer>
    </div>
  )
}

export default LoginScreen
