import { useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import './header.css';

const Header = () => {
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState(JSON.parse(localStorage.getItem('userInfo')));

  const handleLogout = () => {
    // Clear user information from state
    setUserInfo(null);

    // Clear user information from local storage
    localStorage.removeItem('userInfo');

    // Redirect to the login page
    navigate('/login');

    // Show logout confirmation message
    alert('You have been logged out');
  };

  return (
    <div>
      <nav className="main-header navbar navbar-expand bg-info">
        <ul className="navbar-nav ml-auto">
          <li className="nav-item">
            <Link to="/home" className="nav-link" style={{ color: 'black' }}>Home</Link>
          </li>
          <li className="nav-item">
            <Link to="/contact" className="nav-link" style={{ color: 'black' }}>Contact</Link>
          </li>
          <li className="nav-item">
            <Link to="/cart" className="nav-link" style={{ color: 'black' }}>Cart</Link>
          </li>
          {userInfo ? (
            <li className="nav-item dropdown">
              <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                <Link to="/profile" className="dropdown-item">Profile</Link>
                <a className="dropdown-item" onClick={handleLogout}>Logout</a>
              </div>
            </li>
          ) : (
            <li className='nav-item'>
              <Link to="/login" className="nav-link" style={{ color: 'black' }}>Login</Link>
            </li>
          )}
        </ul>
        {/* SEARCH FORM */}
        <form className="form-inline ml-3">
          <div className="input-group input-group-sm">
            <input className="form-control form-control-navbar" type="search" placeholder="Search" aria-label="Search" />
            <div className="input-group-append">
              <button className="btn btn-navbar" type="submit">
                <i className="fas fa-search" />
              </button>
            </div>
          </div>
        </form>
      </nav>
    </div>
  )
}

export default Header;
