import React, {useState,useEffect} from 'react';

import { useParams,useNavigate,useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Button, Card,Form, FormGroup } from 'react-bootstrap';

import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import FormContainer from './FormContainer';

function LoginScreen({location}) {

  const [email,setEmail]=useState('')
  const [password,setPassword]=useState('')

  const redirect = location.search ? location.search.split('=')[1] :'/'
  const navigate = useNavigate();

  const formSubmit = (e) => {
    e.preventDefault();

    const obj = {
        email: e.target.email.value,
        // isactive: customers.isactive,
        password: e.target.password.value,
      
    };



    if (obj.email == '') {
        fieldCheck("Please enter Email");
        document.getElementById("email").focus();
        return true;
    }

    if (obj.password == '') {
        fieldCheck("Please enter Password");
        document.getElementById("password").focus();
        return true;
    }





    //axios
    axios.post("http://127.0.0.1:8000/customers/", obj)
        .then((result) => {
            successnotify()
        }).catch((error) => {
            unsuccessnotify()
        })


}

const successnotify = () => toast.success("Added Successfully !", {
    position: "top-center",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    theme: "light",
});
const unsuccessnotify = () => toast.error("Not Added. Try again !", {
    position: "top-center",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    theme: "light",
});

const fieldCheck = (msg) => toast.error(msg, {
    position: "top-center",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    theme: "light",
});
  return (
    <div>
      <FormContainer>
        <h1>Login</h1>
        <Form>
          <Form.Group controlId='email'>
          <Form.Label>Email Address </Form.Label>
          <Form.Control required type='email' placeholder='Enter Email' value={email} onChange={(e)=> setEmail(e.target.value)}></Form.Control>
          </Form.Group>

          <Form.Group controlId='password'>
                <Form.Label>Password</Form.Label>
                <Form.Control required type='password' placeholder='Enter Password' value={password} onChange={(e)=> setPassword(e.target.value)}></Form.Control>
              </Form.Group>

           <Button className='mt-3' type='submit' variant='primary'>Sign In</Button>
        </Form>

        <Row className='py-3'>
              <Col>
              New Customer? 
              {/* <Link to={redirect?`/register?redirect=${redirect}`:'/register'}>Register</Link> */}
              </Col>

          </Row>
      </FormContainer>
    </div>
  )
}

export default LoginScreen
